# https://leetcode.com/problems/daily-temperatures/submissions/
from typing import List

class Solution:
    def dailyTemperatures(self, temperatures: List[int]) -> List[int]:
        N = len(temperatures)
        result = [0] * N
        start = 0
        end = 1
        while end < N:
            # print(start, end, end=' ')
            if temperatures[start] < temperatures[end]:
                result[start] = end - start
                start += 1
                end = start + 1
            elif end == N - 1:
                start += 1
                end = start + 1
            else:
                end += 1
            # print(result)
            print()

        return result

